import React from 'react'
import YouTube from '@u-wave/react-youtube';
import './PlayTrailer.css';
import { Card, Container, Row, Col, ListGroup } from 'react-bootstrap';
//w: 960, h:540


export default function PlayTrailer(props) {
    let content = [];
    let temp = [];
    function formatMovie() {
        props.movieList.forEach((item, i) => {
            temp.push(item);
            if ((i+1) % 3 === 0) {
                content.push(temp);
                temp = [];
            }
        })
        if (!(temp === [])) {
            content.push(temp);
        }
    }
    if (props.id === null) {
        return(
            <div>
                <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
                <p>Loading...</p>
            </div>
        )
    }
    if (props.id.length === 0) {
        return(
            <div>
                <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
                <p>There is no trailer currently available.</p>


                <Container fluid="md">
                {content.map((item, index) => {
                    return (
                        <Row key={index} className="m-5">
                            {item.map(movie => {
                                let genreMovie = movie.genre_ids;
                                let genreTotal = [];
                                genreTotal = props.genreList.filter((x) => genreMovie.includes(x.id))
                                if (genreTotal.length === 0) {
                                    genreTotal.push({name: "Unidentified :("});
                                }
                                //console.log(movie);
                                let description = movie.overview.split(" ").slice(0, 15);
                                //console.log(description);
                                //console.log(genreTotal);
                                return (
                                    <Col className="d-flex" key={movie.id} sm="4">
                                        <Card className="flex-fill" bg="light" text="dark" border="dark" style={{ width: '18rem' }}>
                                            <Card.Img variant="top" src={`https://image.tmdb.org/t/p/w300_and_h450_bestv2${movie.poster_path}`} />
                                            <Card.Body>
                                                <Card.Title>{movie.original_title}</Card.Title>
                                                <ListGroup variant="flush">
                                                    <ListGroup.Item>
                                                        {description.map((word, index) => {
                                                            if (index === description.length - 1) {
                                                                return (`${word}...`);
                                                            } else return (`${word} `)
                                                        })}
                                                    </ListGroup.Item>
                                                    <ListGroup.Item>
                                                        <span style={{float: "bottom"}}>

                                                            <span style={{float: "right"}}>{movie.release_date.split("-")[0]}</span>
                                                        </span>
                                                    </ListGroup.Item>
                                                    <ListGroup.Item>
                                                        {genreTotal.map((title, index) => {
                                                            if (index === genreTotal.length - 1) {
                                                                return (title.name);
                                                            } else return (`${title.name}, `)
                                                        })}
                                                    </ListGroup.Item>
                                                    <ListGroup.Item>
                                                        <Card.Link href="#" onClick={() => props.Modal(movie.id)}>Movie Information</Card.Link>
                                                    </ListGroup.Item>
                                                </ListGroup>
                                            </Card.Body>
                                        </Card>
                                    </Col>
                                )
                            })}
                        </Row>
                    )
                })}
            </Container>  
            </div>
        )
    }
    console.log(props.id);
    return (
        <div>
            <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
            <div className="trailer" style={{height: `${props.id.length*480}px`}}>
            {props.id.map((item) => {
                return <YouTube className="m-2" key={item.id} video={item.key} width="64%" height="540px" style={{display: "block"}}></YouTube>
            })}
            </div>
        </div>
    )
}
