import React from 'react'
import YouTube from '@u-wave/react-youtube';
import './PlayTrailer.css';
import { Card, Container, Row, Col, ListGroup } from 'react-bootstrap';
import { render } from '@testing-library/react';
//w: 960, h:540


export default function PlayTrailer(props) {
    //console.log(JSON.stringify(props));


    if (props.id === null) {
        return(
            <div>
                <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
                <p>Loading...</p>
            </div>
        )
    }
    if (props.id.length === 0) {
        return(
            <div>
                <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
                <p>There is no movie information available.</p>                
            </div>
        )
    }

    if(props.release.length !== null){
            return(
                <div classNmae="release">
                    {props.release.map(item =>
                        <li key = "{item.release_date}"></li>
                    )}
                </div>
            )
    }

    console.log(props);
    return (
        <div>
            <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
            <div className="trailer" style={{height: `${props.id.length*200}px`}}>
            {props.id.map((item) => {
                return (<span>{item.content}</span>)
            })}
            </div>

        </div>
    )
}
