import React from 'react'
import YouTube from '@u-wave/react-youtube';
import './PlayTrailer.css';

//w: 960, h:540


export default function PlayTrailer(props) {
    let genreMovie = movie.genre_ids;
    let genreTotal = [];
    genreTotal = props.genreList.filter((x) => genreMovie.includes(x.id))
    if (genreTotal.length === 0) {
        genreTotal.push({name: "Unidentified :("});
    }
    if (props.id === null) {
        return(
            <div>
                <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
                <p>Loading...</p>
            </div>
        )
    }
    if (props.id.length === 0) {
        return(
            <div>
                <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
                <p>There is no trailer currently available.</p>
                {/* <ListGroup.Item>
                            {genreTotal.map((title, index) => {
                                if (index === genreTotal.length - 1) {
                                    return (title.name);
                                } else return (`${title.name}, `)
                            })}
                </ListGroup.Item> */}
            </div>
        )
    }
    console.log(props.id);
    return (
        <div>
            <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
            <div className="trailer" style={{height: `${props.id.length*480}px`}}>
            {props.id.map((item) => {
                return <YouTube className="m-2" key={item.id} video={item.key} width="64%" height="540px" style={{display: "block"}}></YouTube>
            })}
            </div>
        </div>
    )
}
