import React from 'react'
import YouTube from '@u-wave/react-youtube';
import './PlayTrailer.css';
import { Card, Container, Row, Col, ListGroup } from 'react-bootstrap';
import { render } from '@testing-library/react';
//w: 960, h:540

export default function PlayTrailer(props) {
    //console.log(JSON.stringify(props.release));
    // fetch(`http//localhost:8080/genres`)
    // .then(response => console.log(response))
    // .then(json => console.log(json))
    this.state = {
        id: '',
        name: ''
      };
    fetch(`http//localhost:8080/genres`).then(res => res.json())
    .then((result) => {
        this.setState({
            id: result.id,
            name: result.name
        });
    });


    //console.log(id);
    //document.write(<span>{props.release}</span>);
    //console.log(JSON.stringify(props.release.release_dates));
    var actors = [];
    if (props.credits){
                console.log(props.credits);
                {props.credits.map((item) => {
                     //return (<div>{item.name}</div>)
                    //console.log(item.name)
                    actors = actors.concat(item.name);
                })}                          
                console.log(actors)
                if(actors.length === 0){
                    actors = actors.concat("N/A");
                }
    }

    var original_lg = "N/A"; 
    var genre_list = [];
    if (props.genre){        
        {props.genre.map((item) => {
           genre_list = genre_list.concat(item.name);
       })}           

    }

    return (
        // <span>{props.credits}</span>        
        <div>            
            <button className="mb-2" style={{display: "block"}} onClick={() => props.close()}>Close</button>
            <h3> Movie Information </h3>
            <h4>Title: </h4>
            <p>{`${props.details[0]}`}</p>
            <h4>Run Time:</h4>
            <p>{`${props.details[1]}`} minutes</p>
            <h4>Language: </h4>
            <p>{`${props.details[2]}`}</p>
            <h4>Overview: </h4>
            <p>{`${props.details[3]}`}</p>
            <h4>Release Date: </h4>
            <p>{`${props.details[4]}`}</p>
            <h4>Poster Path: </h4>
            <p>{`${props.details[5]}`}</p>
            <h4>Vote Average: </h4>
            <p>{`${props.details[6]}`}</p>
            <h4>Genres: </h4>
            {genre_list.map((item) => {                 
                 return (<p style={{display : 'inline'}}>
                    {`${item}, `}
                 </p>);
             })} 
            <h4>actors: </h4>            
             {actors.map((item) => {                 
                 return (<p style={{display : 'inline'}}>
                    {`${item}, `}
                 </p>);
             })} 
             {/* return props.credits() */}
        </div>
    )

}
