    details = details.concat(result.poster_path);
    details = details.concat(result.vote_average);
    get_genre = get_genre.concat(result.genres);
    console.log(get_genre);
    url = `https://api.themoviedb.org/3/movie/${id}}/credits?api_key=${apiKey}&language=en-US`;
    data = await fetch (url);
    result = await data.json();
    setCredits(result.cast);

  }

  useEffect(() => {
    getGenre();
  }, [])
  if (movieList === null) {
    return (
      <div>
        Loading...
      </div>
    )
  }
  return (

    <div>

      {/* TOP NAVIGATION BAR */}
      <Navbar className="navbar-color" variant="dark" expand="lg" sticky="top">
        <Navbar.Brand href="#home">CineFile</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">     

            <Nav.Link href="#toprated" onClick={() => getTopRated()}>Top Rated</Nav.Link>    

            <NavDropdown title="Genres" id="basic-nav-dropdown">
              {genre.map((item) => {
                return(
                <NavDropdown.Item key={item.id} onClick={() => sortMovieGenre(item.id)}>{item.name}</NavDropdown.Item>
                );
              })}
            </NavDropdown>

          </Nav>

          <Form inline>
            <FormControl id="keyword" onChange={field => {searchContent = field.target.value;}} type="text" placeholder="Search" className="mr-sm-2" />
            <Button variant="dark" onClick={() => getKeyword(searchContent)}>Search</Button>
          </Form>
        </Navbar.Collapse>
      </Navbar> 

      <form className="form">
        <InputRange
          draggableTrack
          step={1}
          maxValue={10}
          minValue={1}
          value={rating}
          onChange={value => setRating(value)}
          onChangeComplete={() => filterMovieRating(rating)} className="input-range"/>
      </form>

      <Container>
        <MovieList movieList = {movieList} genreList = {genre} Modal={openModal}/>
        <ReactModal isOpen={modalOpen} ariaHideApp={false} onAfterOpen={() => getMovieInfo(movieId)} closeTimeoutMS={1000}>
          <PlayTrailer close={closeModal} details={details} review={reviews} credits={getCredits} genre = {get_genre} actor = {actors}></PlayTrailer>
        </ReactModal>
      </Container>

      <Container className="seemore">
        <Button variant="dark" className="m-3 mb-5" onClick={() => getSeeMore(page-1)}>Previous</Button>
        <Button variant="dark" className="m-3 mb-5" onClick={() => getSeeMore(page+1)}>Next</Button>
      </Container>
    </div>
  );
}

export default App;
